import Button from 'react-bootstrap/Button';
import './styles/calendar.css';

class Calendar extends React.Component { 

	constructor(){
		super();
		this.state = {currentMonth: '1', currentMonthString: '', events: [{'id': 'abdf34', 'date': '1-1-2022', 'event': 'go to grocery store' }
		, {'id': 'fhgh5a', 'date': '1-1-2022', 'event': 'bake cake for potluck'}]}
	}


  handleMonthString(){

  switch (this.state.currentMonth){
    case '1':
    this.setState({currentMonthString: 'January'});
    break;
    case '2':
    this.setState({currentMonthString: 'February'})
    break;
    case '3':
    this.setState({currentMonthString: 'March'})
    break;
    case '4':
    this.setState({currentMonthString: 'April'})
    break;
    case '5':
    this.setState({currentMonthString: 'May'})
    break;
    case '6':
    this.setState({currentMonthString: 'June'})
    break;
    case '7':
    this.setState({currentMonthString: 'July'})
    break;
    case '8':
    this.setState({currentMonthString: 'August'})
    break;
    case '9':
    this.setState({currentMonthString: 'September'})
    break;
     case '10':
    this.setState({currentMonthString: 'October'})
    break;
     case '11':
    this.setState({currentMonthString: 'November'})
    break;
     case '12':
    this.setState({currentMonthString: 'December'})
    break;


  }



  }




      // this.state.currentMonth == '2'? <div></div>: else: this.state.currentMonth in('4', '6', '11'): two more claendar squares: else - 3 more
      // or pretened every month has same amount of days




      handleTesting = () => {


 var currentMonth = this.state.currentMonth;
      var currentIntMonth = parseInt(currentMonth);
      if (currentIntMonth == 12){
        currentIntMonth = 1;
      }
      else {
        currentIntMonth = currentIntMonth + 1;
      }

      currentMonth = String(currentIntMonth);
      this.setState({currentMonth: currentMonth});
      this.handleMonthString();

}


     handleNextMonth = () => {

      var currentMonth = this.state.currentMonth;
      var currentIntMonth = parseInt(currentMonth);
      if (currentIntMonth == 12){
        currentIntMonth = 1;
      }
      else {
        currentIntMonth = currentIntMonth + 1;
      }

      currentMonth = String(currentIntMonth);
      this.setState({currentMonth: currentMonth});



     }

      handleAdd = (date, name ) => {

        var allEvents = this.state.events.slice();
        const id = (Math.random() + 1).toString(36).substring(7);
        alert(id);
        allEvents.push({"id": id, "date": date, "event": name});
        this.setState({events: allEvents});



      }



     handleChange = (id, text) => {

 var allEvents = this.state.events.slice();
         for (var i in allEvents) {
     if (allEvents[i].id == id) {
        allEvents[i].event = text;
        break;

     }

   }
        this.setState({events: allEvents});



     }



     handleDelete = (id) => {

       var allEvents = this.state.events.slice();

      allEvents = allEvents.filter(task => task.id !==  id);
      this.setState({events: allEvents});




     }

	render() { 


       return(

       <div><h1>{this.state.currentMonthString}</h1>
       <Button className='prev' variant="outline-dark">prev</Button>
      <Button className='next' variant="outline-dark">next</Button>
         <button onClick={this.handleNextMonth}>testing</button>
      	<div style={{ display: 'flex',

    flexDirection: 'row', flexBasis: '100%'}}>


        <CalendarSquare date={this.state.currentMonth.concat('-1', '-2022')} events={this.state.events} handleAdd={this.handleAdd} handleChange={this.handleChange} handleDelete = {this.handleDelete} />
       <CalendarSquare date={this.state.currentMonth.concat('-2', '-2022')} events={this.state.events} handleAdd={this.handleAdd} handleChange={this.handleChange} handleDelete = {this.handleDelete} />
        <CalendarSquare date={this.state.currentMonth.concat('-3', '-2022')} events={this.state.events} handleAdd={this.handleAdd} handleChange={this.handleChange} handleDelete = {this.handleDelete} />
       	</div>


       </div>


      );
	} 

}


export default Calendar;
