import CalendarSquare from './CalendarSquare';

class Calendar extends React.Component { 

	constructor(){
		super();
		this.state = {currentMonth: '1', events: [{'id': 'abdf34', 'date': '1-1-2022', 'event': 'go to grocery store' }
		, {'id': 'fhgh5a', 'date': '1-1-2022', 'event': 'bake cake for potluck'}]}
	}



      handleTesting = () => {

          this.setState({...this.state.events[0], event: "drive to grocery store"});
      }


     handleChange = (id, text) => {

          // edit event
          const allEvents = this.state.events;


 for (var i in allEvents) {
     if (allEvents[i].id == id) {
        allEvents[i].event = text;
        break;

     }

   }

   this.setState({...this.state.events[0], event: text});

    this.setState({events: allEvents});




     }



     handleDelete = (id) => {



     }

	render() { 


       return(

       <div><h1>January</h1>
<button onClick={handleTesting}>test</button>

      	<div style={{ display: 'flex',
    flexDirection: 'row', flexBasis: '100%'}}>


        <CalendarSquare date={this.state.currentMonth.concat('-1', '-2022')} events={this.state.events} handleChange={this.handleChange}/>
       <CalendarSquare date={this.state.currentMonth.concat('-2', '-2022')} events={this.state.events}/>
        <CalendarSquare date={this.state.currentMonth.concat('-3', '-2022')} events={this.state.events}/>
       	</div>


       </div>


      );
	} 

}


export default Calendar;
