import React from 'react';
import Card from 'react-bootstrap/Button';


class CalendarSquare extends React.Component {

	constructor(){
		super();
		this.state = {currentEvents: []};
	}

componentDidMount(){

const passedinDate = this.props.date;
const events = this.props.events;
const currentEvents = this.state.currentEvents;
for (var i = 0; i < events.length; i++){
	const eachEevent = events[i].event;
	const date = events[i].date;
	if (date == this.props.date){
		currentEvents.push(eachEevent);
	}
}

this.setState({currentEvents: currentEvents});
}

render(){

	return(<div>


          <Card style={{ width: '14rem', height: '14rem', marginTop: '60px', marginLeft: '10px',  backgroundColor: '#007bff!important' }}>
           {this.state.currentEvents}
          </Card>


		</div>);
}}

export default CalendarSquare;
