import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form'
import './styles/calendarsquare.css';


class CalendarSquare extends React.Component {

	constructor(){
		super();
		this.state = {currentEvents: [], number: 0, editDialog: false};

	}

componentDidMount(){

// add event
// add button
// dialog form div with line listed


const passedinDate = this.props.date;
const number = passedinDate.split("-", 2);
// set state here
this.setState({number: number[1]});
const events = this.props.events;
const currentEvents = this.state.currentEvents;
for (var i = 0; i < events.length; i++){
	const eachEvent = events[i].event;
	const date = events[i].date;
	if (date == this.props.date){
		currentEvents.push({'date': date, 'name': eachEvent});
	}
}

this.setState({currentEvents: currentEvents});
}

handleEdit = () => {

this.setState({editDialog: true});

}


render(){

const cardText = 
           this.state.number + "\n" + "\n";


	return(<div>






  <div className="card" style={{ width: '14rem', height: '14rem', marginTop: '60px', marginLeft: '10px'}}>
  <div>

           {cardText}
     {this.state.currentEvents.map(function(d, idx){
         return (<p className='text'>{d.name}
         	</p>



         	)
       })}



   </div>  




<img onClick={this.handleEdit} src="https://img.icons8.com/ios-glyphs/10/000000/edit--v1.png"/>

<Form className='form'>


  <Form.Group className="mb-3" controlId="formBasicPassword">
    <Form.Control type="text" placeholder="new event here..." />
  </Form.Group>
  <Button variant="primary" type="submit">
    add event 
  </Button>
</Form>













</div>











		</div>);
}}

export default CalendarSquare;
